                            selecthtml.push("'>");
                            selecthtml.push(rslt.data[groups[g]].campaigns[campaigns[i]].gems + " " + g_js_strings.commonstr.gems + " (" + g_paymentSymbol + rslt.data[groups[g]].campaigns[campaigns[i]].price + ")");
